using Microsoft.VisualStudio.TestTools.UnitTesting;using DocDocos;using System.Xml;using System;using System.IO;namespace MokaDocosTest{    [TestClass]    public class AjouterToutTest    {        [TestMethod]        public void FichierComplexe()        {            DocDocos.IO monIO = new DocDocos.IO();            DocDocosDA monGenerateur = new DocDocosDA();            string Temp = monIO.AjouterCheminFichier(                monIO.RepertoireAssembly(),                "Ressources");            monGenerateur.FichierXMLDoc = monIO.AjouterCheminFichier(                Temp, "JCAssertionnCore.xml");            monGenerateur.AjouterToutDict ();            EntiteDocument Resultat =                monGenerateur.GetEntiteDoc("JCAssertionCore.JCACompare.Compare(System.Int64,System.String,System.Int64)");            Assert.AreEqual(                "JCAssertionCore.JCACompare.Compare(System.Int64,System.String,System.Int64)",                 Resultat.Nom,                "Erreur : " + Resultat.Nom);            Assert.AreEqual(                "JCAssertionCore", Resultat.Niveau(0),                "Erreur : " + Resultat.Niveau(0));            Assert.IsTrue(Resultat.Sommaire.Contains(                "Compare deux entier avec un operateur"),                "Erreur : " + Resultat.Sommaire );                    }    }}